import React ,{useState, useEffect}from 'react'
import Button from '@material-ui/core/Button';
import userEvent from '@testing-library/user-event';
import axios from 'axios';
import AddPost from './AddPost';
function Community() {
    const [posts, setPosts] = useState([]);
    useEffect(async () => {
       await axios.get('http://localhost:8080/allposts')
       .then((data)=>setPosts(data.data))
       .catch((e)=>console.log(e))
    }, [])
    const likehandler=(async (id)=>{
        await axios.post('http://localhost:8080/like',{id})
        .catch((e)=>console.log(e));
        console.log("liked");
    })
    const unlikehandler=(async (id)=>{
        await axios.post('http://localhost:8080/unlike',{id})
        console.log("unliked");
    })
    return (
        <div style={{marginLeft:500,marginTop:20}}>
           <AddPost />
           {posts.length===0 && <h3>Loading Posts</h3>}
           {posts.map((post)=>{
               return (
                   <div style={{width:"400px",padding:"5px",border:"2px solid black"}}>
                    {post.user.name}
                    <h6>{post.time.slice(11,16)}</h6>
                   <h4>{post.content}</h4>
                    <button onClick={()=>likehandler(post._id)}>Like</button> <button onClick={()=>unlikehandler(post._id)}>Unlike</button>
                    <br></br>
                   {post.likes.length} likes {post.comments.length} comments
                   <br></br>
                   <input type="text"></input>
                   <button>Add comment</button>
                   </div>
               )
           })}
        </div>
    )
}

export default Community
