import React from 'react'
import {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from 'axios'
import { Button, Form, FormGroup, Label, Input } from 'reactstrap'
class Login extends Component
{
    constructor()
    {
        super()
        this.state={
            rollno:'',
            password:'',
        }
    }
    rollnoChange=(event)=>{
        this.setState({
            rollno:event.target.value
        })
    }
    passwordChange=(event)=>{
        this.setState({
            password:event.target.value
        })
    }

    submitchange=async (event)=>
    {
        event.preventDefault();
        console.log(this.state);
        await axios.post('http://localhost:8080/users/login',this.state)
        .then((data)=>{
            console.log("sent login page") 
            console.log(data);
            this.props.history.push('/dashboard')
        })
        .catch((err)=>console.log("ohh somee error  "+err))
    }
    render()
    {
        return (
            <div style={{backgroundColor: "lightblue",height:"240px",width:"300px" ,margin:"110px auto", padding:"20px"}}>
            <p><h1 style={{textAlign:"center"}}>Login</h1></p>
            <form onSubmit={this.submitchange}>
                Roll No:: : <input type="Number" name="roll" value={this.state.rollno} onChange={this.rollnoChange}/><br/><br/>
                Password : <input type="Password" name="pass" value={this.state.password} onChange={this.passwordChange}/><br/><br/>
                <button type="Submit">Submit</button>
            </form>         
        </div>
        )
    }
}
export default Login;
