import React ,{useState, useEffect,useContext}from 'react'
import Button from '@material-ui/core/Button';
import userEvent from '@testing-library/user-event';
import axios from 'axios';
import AddPost from './AddPost';
import authContext from '../context/AuthContext';
function Community() {
    const {user}=useContext(authContext);
    const [posts, setPosts] = useState([]);
    const [comment, setComment] = useState('');
    useEffect(async () => {
       await axios.get('http://localhost:8080/allposts')
       .then((data)=>setPosts(data.data.reverse()))
       .catch((e)=>console.log(e))
    }, [posts])
    const likehandler=(async (id)=>{
        await axios.get('http://localhost:8080/like/'+id)
        .then((data)=>setPosts(data.data.reverse()))
        .catch((e)=>console.log(e));
    })
    const unlikehandler=(async (id)=>{
        await axios.get('http://localhost:8080/unlike/'+id)
        .then((data)=>setPosts(data.data.reverse()))
        .catch((e)=>console.log(e));
    })
    const commenthandler=(async (id)=>{
            await axios.put('http://localhost:8080/addcomment/'+id,{comment})
            .then((data)=>{
                setPosts(data.data.reverse())
                setComment('');
            })
            .catch(e=>console.log(e))
    })
    const deletehandler=(async (id)=>{
        await axios.delete('http://localhost:8080/delete/'+id)
        .then((data)=>setPosts(data.data))
        .catch(e=>console.log(e))
    })
    return (
        // 
        <div style={{marginLeft:500,marginTop:20}}>
           <AddPost />
           {posts.length===0 && <h3>Loading Posts</h3>}
           {posts.map((post)=>{
               return (
                   <div style={{width:"400px",padding:"5px",border:"2px solid black"}}>
                    {post.user.name}                  
                    <h6>{post.time.slice(11,16)}</h6>
                   <h4>{post.content}</h4>
                   {
                       post.likes.includes(user._id)?<button onClick={()=>unlikehandler(post._id)}>Unlike</button>
                       :
                       <button onClick={()=>likehandler(post._id)}>Like</button> 
                   }
                    <br></br>
                   {post.likes.length} likes {post.comments.length} comments
                   <br></br>
                   <form onSubmit={(e)=>{
                       commenthandler(post._id)
                       e.preventDefault();
                       }
                       }>
                        <input type="text" value={comment} onChange={(e)=>setComment(e.target.value)}></input>
                        <button type="submit">Add comment</button>   
                   </form>
                   <a href="#" onClick={()=>deletehandler(post._id)}>Delete Post</a>
                   &emsp;
                   &emsp;
                   &emsp;
                   <a href={`/post/${post._id}`}>All comments</a>
                   </div>

               )
           })}








        </div>
    )
}

export default Community
