import React, {useState,useEffect} from 'react'
import Form from 'react-bootstrap/Form'
import axios from 'axios'
import Button from 'react-bootstrap/Button'
function Update(props) {
    const id=props.match.params.id;
    console.log(id);
    const [user, setUser] = useState({
        name:'',roll:'',branch:'',mail:'',mob:'',year:''
    })
    useEffect(() => {
        async function fetchAPI()
        {
            await axios.get('http://localhost:8080/users/'+id)
            .then((data)=>
            {
                user=setUser(data.data[0]);
                console.log(user);
            })
            .catch((err)=>console.log("oh"+err))
        }
        fetchAPI();
    }, [])
    return (
        <div className='container'>
            <Form>
                <Form.Group controlId="formBasicEmail">
                    <Form.Label>Name</Form.Label>
                    <Form.Control type="name" value={user.name} onChange={(e)=>setUser(...user,user.name=e.target.value)}/>
                    <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                    </Form.Text>
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" />
                </Form.Group>
                <Form.Group controlId="formBasicCheckbox">
                    <Form.Check type="checkbox" label="Check me out" />
                </Form.Group>
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>
        </div>
    )
}

export default Update
