import React ,{useState,useEffect} from 'react'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
function ChangeProfile() {
    const id=props.match.params.id;
    const [selectedFile, setSelectedFile] = useState(null)
    function submithandler()
    {
        const formData = new FormData()
        formData.append(
            'myFile',
            selectedFile,
            selectedFile.name
        )
        axios.post('http://localhost:8080/users/'+id+'/profileUpdate', formData)
    }
    return (
        <div>
           <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src="https://static.toiimg.com/thumb/msid-73540687,width-800,height-600,resizemode-75,imgsize-575035,pt-32,y_pad-40/73540687.jpg" />
                <Card.Body>
                    <Card.Title>Profile Photo</Card.Title>
                {/* <Button variant="primary"></Button> */}
                <form onSubmit={submithandler}>
                    <input type="file" onChange={(e)=>setSelectedFile(e.target.files[0])}/>
                </form>
                <Button type="submit" variant="primary">Submit</Button>
            </Card.Body>
            </Card>
        </div>
    )
}

export default ChangeProfile
